import axios from "axios"

axios.defaults.baseURL = Http.API_BASE_URL + "/api/"
axios.defaults.withCredentials = true

const Search = ({ field, query}) => {
  return axios.get(`api/search/books/${field}/${query}`)
}

export default {
  Search
}
